            .query(querysSOLICITUDES_OFERTAS_LABORALES.getJobsByStudent);
        if (result.recordset.length == 0) {
            res.json({
                message: 0,
            });
        } else {
            res.json(result.recordset);
        }
    } catch (error) {
        res.send(error.message);
    }
}

