};

export const getStatsFacultades = async (req, res) => {
    try {
        const pool = await getConnection();
        const result = await pool
            .request()
            .query(querysSTATS.getStatsByFacultad);
        if (result.recordset.length == 0) {
            res.json({
                message: "Sin datos",
